import React, { useState, useEffect, Component, } from 'react'
import styled from "styled-components"
export default function ToDo() {
    const [tasks, setTasks] = useState([
        {
            id: 1,
            title: "Buy 1 kg Tomato",
        },
        {
            id: 2,
            title: "Buy 2 kg Onion",
        },
        {
            id: 3,
            title: "Visit Friend",
        },
        {
            id: 4,
            title: "Clean House",
        },
    ]);
    const [completed, setCompleted] = useState([
        {
            id: 5,
            title: "Washing Clothes",
        },
        {
            id: 6,
            title: "Play Cricket",
        },
        {
            id: 7,
            title: "1 Km Walking",
        },
        {
            id: 8,
            title: "Do HomeWork",
        },
    ]);

    const [newTask, setNewTask] = useState("");
    const [itemCount, setItemCount] = useState(0);

    useEffect(() => {
        setItemCount(completed.length + tasks.length);
    }, []);

    const deleteTask = (id) => {
        let new_list = tasks.filter((task)=> task.id !== id);
        setTasks(new_list);
    }

    const deleteCompleted = (id) => {
        let new_list = completed.filter((task)=> task.id !== id);
        setCompleted(new_list);
    }

    const completeTask = (id) => {
        let current_task = tasks.find((task)=> task.id == id);
        setCompleted([...completed,current_task]);

        let new_list = tasks.filter((task)=> task.id !== id);
        setTasks(new_list);
    }

    const revertTask = (id) => {
        let current_task = completed.find((task)=> task.id == id);
        setTasks([...tasks,current_task]);

        let new_list = completed.filter((task)=> task.id !== id);
        setCompleted(new_list);
    }
    const renderTasks = () =>{
        return tasks.map((task)=>(
            <ListItem key={task.id}>
                <LeftContainer onClick={()=>completeTask(task.id)}>
                    <CheckContainer></CheckContainer>
                    <ItemContent>{task.id}, {task.title}</ItemContent>
                </LeftContainer>
                <ItemRightContainer>
                    <ActionButton onClick={()=>deleteTask(task.id)}>
                        <ButtonImage src={require("./assets/delete.svg").default} alt="Delete"/>
                    </ActionButton>
                </ItemRightContainer>
            </ListItem>
        ));
    };
    const renderCompleted = () =>{
        return completed.map((task)=>(
            <ListItem key={task.id}>
                <LeftContainer>
                    <CheckContainerCompleted>
                        <TickImage src={require('./assets/tick-green.svg').default} alt="Tick Image"/>
                    </CheckContainerCompleted>
                    <ItemContentCompleted>{task.id}, {task.title}</ItemContentCompleted>
                </LeftContainer>
                <ItemRightContainer>
                    <ActionButton onClick={()=>revertTask(task.id)}>
                        <ButtonImage src={require("./assets/revert.svg").default} alt="Revert"/>
                    </ActionButton>
                    <ActionButton onClick={()=>deleteCompleted(task.id)}>
                        <ButtonImage src={require("./assets/delete.svg").default} alt="Delete"/>
                    </ActionButton>
                </ItemRightContainer>
            </ListItem>
        ));
    };
    const addNewTask = (event) => {
        event.preventDefault();

        let new_task = {
            id: itemCount + 1,
            title: newTask,
        }
        setTasks([...tasks,new_task]);
        setNewTask("");
        setItemCount((prev) => prev + 1);
    };
  return <Container>
      <Heading> ToDo List </Heading>
      <ToDoContainer>
        <SubHeading>Things to be done</SubHeading>
        <ToDoList>{renderTasks()}</ToDoList>
      </ToDoContainer>
      <NewToDoForm>
          <FormInput  value={newTask} onChange={(e)=> setNewTask(e.target.value)} placeholder="Type New Task..."/>
          <FormSubmitButton onClick={(e)=>addNewTask(e)}>Add New Text</FormSubmitButton>
      </NewToDoForm>
      <ToDoContainer>
        <SubHeading>Things to be done</SubHeading>
        <ToDoList>{renderCompleted()}</ToDoList>
      </ToDoContainer>
  </Container>;
};

const Container = styled.div`
    width: 90% auto;
    padding: 50px 10%;
    max-width: 1000px;
    border-left: 2px solid #f5f5f5;
    border-right: 2px solid #f5f5f5;
    min-height: 100vh;
    margin: 0 auto;
`;
const Heading = styled.h1`
    font-size: 52px;
    font-weight: bold;
    text-align: center;
    margin-bottom: 40px;
`;
const ToDoContainer = styled.div``;
const SubHeading = styled.h3`
    font-size: 36px;
    color: #050241;
`;
const ToDoList = styled.ul``;
const ListItem = styled.li`
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 20px;
`;
const LeftContainer = styled.div`
    display: flex;
    align-items: center;
`;
const CheckContainer = styled.span`
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 2px solid #050241;
    display: inline-block;
    margin-right: 15px;
    cursor: pointer;
`;
const ItemContent = styled.span`
    cursor: pointer;
    font-size: 28px;
`;
const ItemRightContainer = styled.div``;
const ActionButton = styled.button`
    border: none;
    background: none;
    cursor: pointer;
    margin-right: 20px;
    outline: none;
    &:last-child{
        margin-right: 0;
    }
`;
const ButtonImage = styled.img``;
const NewToDoForm = styled.form`
    display: flex;
    margin-left: 40px;
    margin-top: 30px;
    position: relative;
    &::before{
        content: "";
        background: url(${require("./assets/plus.svg").default});
        width: 16px;
        height: 16px;
        display: block;
        position: absolute;
        left: 10px;
        top: 0;
        bottom: 0;
        margin: auto 0;
        z-index: 2;
    }
`;
const FormInput = styled.input`
    display: block;
    width: 100%;
    outline: none;
    border: 1px solid #c6c6c6;
    padding: 0 10px 0 35px;
    font-variant: 22px;
`;
const FormSubmitButton = styled.button`
    white-space: nowrap;
    padding: 15px 25px;
    border: none;
    color: #fff;
    background: #050241;
    cursor: pointer;
    border-radius: 6px;
    border-top-left-radius:0 ;
    border-bottom-left-radius:0 ;
    font-size: 24px;
`;
const CheckContainerCompleted = styled(CheckContainer)`
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-color: #06c692;
`;
const ItemContentCompleted = styled(ItemContent)`
    color: #06c692;
`;
const TickImage = styled.img``;
